import React from 'react'

const MovieCard = ({movie}) => {
    return (
            <div className='d-flex'>
                {movie.poster_path ? (
                    <img src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`} alt={`${movie.title} Poster`} />
                ) : (
                   <div className='filler-poster'></div>
                )}
            </div>
    )
}

export default MovieCard
