import React, { useState, useEffect } from 'react'
import TextField from '@material-ui/core/TextField'
import { makeStyles, withTheme } from '@material-ui/core/styles';
import 'bootstrap/dist/css/bootstrap.min.css';

const useStyles = makeStyles((theme) => ({
    root: {
        '& > *': {
            margin: theme.spacing(1),
            width: '25ch',
        },
        
    }
}));

const Home = () => {

    const [movies, setMovies] = useState([]);
    const [searchValue, setSearchValue] = useState('');
    const classes = useStyles();

    const getMovies = async (searchValue) => {
        const apiKey = 'dfbf0d2ccd7a5faa63a22c477095fca1'
        const url = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false&query=${searchValue}`;

        const response = await fetch(url);
        const responseJson = await response.json();

        if (responseJson.Search) {
            console.log(responseJson)
        }
    };

    useEffect(() => {
        getMovies(searchValue);
    }, [searchValue]);

    return (
        <div className='container'>
            <div className='row'>
                <div className='col-md-6 offset-md-3'>
                <TextField width='100%' className={classes.textField} id="filled-basic" label="Search Movies" variant="filled" />
                </div>
            </div>
        </div>
    )
}

export default Home
