import React, { useState} from 'react'
import TextField from '@material-ui/core/TextField'
import { makeStyles} from '@material-ui/core/styles';
import MovieCard from './MovieCard';

const useStyles = makeStyles((theme) => ({

    FormControl: {
        width: 500,
        marginTop: '100px',
        backgroundColor: "gray"
    },
    
}));

const Home = () => {

    const [results, setResults] = useState([]);
    const [searchValue, setSearchValue] = useState('');
    const classes = useStyles();

    const getMovies = async (e) => {

        setSearchValue(e.target.value);

        const apiKey = 'dfbf0d2ccd7a5faa63a22c477095fca1'
        const url = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false&query=${e.target.value}`;

        const response = await fetch(url);
        const responseJson = await response.json();

        if (!responseJson.errors) {
            setResults(responseJson.results);
        } else {
            setResults([]);
        }
    };


    return (

        <div className='container'>
            <div className='row'>
                <div className='col-md-6 offset-md-3'>
                    <TextField
                    className={classes.FormControl} 
                    label="Search Movies" variant="filled" 
                    value={searchValue} 
                    onChange={getMovies}
                    />
                </div>
            </div>
            {results.length > 0 && (
                <ul list-group list-group-horizontal>
                    {results.map(movie => (
                        <li key={movie.id}>
                            <MovieCard  movie={movie}/>
                        </li>
                    ))}
                </ul>
            )}
        </div>

    )
}

export default Home
