import React, { useState} from 'react'
import TextField from '@material-ui/core/TextField'
import { makeStyles, withTheme } from '@material-ui/core/styles';
import { Grid } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({

    FormControl: {
        width: 500,
        marginTop: '100px',
        backgroundColor: "gray"
    },
    
}));

const Home = () => {

    const [movies, setMovies] = useState([]);
    const [searchValue, setSearchValue] = useState('');
    const classes = useStyles();

    const getMovies = async (searchValue) => {
        const apiKey = 'dfbf0d2ccd7a5faa63a22c477095fca1'
        const url = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false&query=${searchValue}`;

        const response = await fetch(url);
        const responseJson = await response.json();

        if (responseJson.Search) {
            console.log(responseJson)
        }
    };


    return (

        <Grid
            container
            spacing={0}
            direction="column"
            alignItems="center"
        >
            <Grid item xs={3}>
                <TextField 
                className={classes.FormControl} 
                label="Search Movies" variant="filled" value={searchValue} onChange={(e) => setSearchValue(e.target.value) }/>
            </Grid>
        </Grid>


    )
}

export default Home
