import React, { useState, useEffect } from 'react'
import classes from '@material-ui/core/classes'
import TextField from '@material-ui/core/classes'

const Home = () => {

    const [movies, setMovies] = useState([]);
    const [searchValue, setSearchValue] = useState('');

    const getMovies = async (searchValue) => {
        const apiKey = 'dfbf0d2ccd7a5faa63a22c477095fca1'
        const url = `https://api.themoviedb.org/3/search/movie?api_key=${apiKey}&language=en-US&page=1&include_adult=false&query=${searchValue}`;

        const response = await fetch(url);
        const responseJson = await response.json();

        if (responseJson.Search) {
            console.log(responseJson)
        }
    };

    useEffect(() => {
        getMovies(searchValue);
    }, [searchValue]);

    return (
        <form className={classes.root} noValidate autoComplete="off">
            <TextField id="standard-basic" label="Standard" />
            <TextField id="filled-basic" label="Filled" variant="filled" />
            <TextField id="outlined-basic" label="Outlined" variant="outlined" />
        </form>
    )
}

export default Home
