import React from 'react'

const MovieCard = ({movie}) => {
    return (
            <>
                {movie.poster_path ? (
                    <img className='mt-3' style={{height:'300px'}} src={`https://image.tmdb.org/t/p//${movie.poster_path}`} alt={`${movie.title} Poster`} />
                ) : (
                   <div className='filler-poster'></div>
                )}
            </>
    )
}

export default MovieCard
